import React, { Component } from 'react';
import book from '../images/book.svg';
import '../style/Gallery.css';

// TODO: Can this component be functional instead?
class Gallery extends Component {

  render() {
    return (
      <div>{
        this.props.items.map( (item, index) => {
          const { volumeInfo } = props.info;
          const thumbNail = volumeInfo.hasOwnProperty('imageLinks') == false ? "https://vignette.wikia.nocookie.net/pandorahearts/images/a/ad/Not_available.jpg/revision/latest?cb=20141028171337" : volumeInfo.imageLinks.thumbnail;
          let { title, imageLinks, infoLink, authors, publishYear } = item.volumeInfo;
          return (
            <a
              key={index}
              className="book"
              href={infoLink}
              target="_blank"
              rel="noopener"
              >
              <div className="container">
                {/*<img
                  src={undefined !== imageLinks ? imageLinks.thumbnail : {book}}
                  alt={`Pictured: The cover for the book ${title}.`}
                  className="bookCover"
                />*/}
                <img src={thumbNail} alt=""/>
                <div className="desc">
                  <h2>{title}</h2>
                  <h3>Author: {authors}</h3>
                  {/*<p>Published: {publishYear == "0000" ? "Not available" : publishYear.substring(0,4)}</p>*/}                    
                </div>
              </div>

              {/*<div className="bookTitle">
                <header>{title}</header>
                <h2>{author}</h2>
              </div>*/}
            </a>
          )
        })
      }</div>
    )
  }
}

export default Gallery;
