import React, { Component } from 'react';
import Navitem from './Navitem';
import Browse from './Browse';
import { Dropdown } from 'react-bootstrap';
import '../style/util.css';
import '../index.css';

class Navbar extends React.Component {
    state = {
        search: '',
        searching: false
    }

    constructor(props)
    {
        super(props);
        this.state = {
            'NavItemActive':''
        };
    }
    activeitem=(x)=>
    {
        if(this.state.NavItemActive.length>0){
            document.getElementById(this.state.NavItemActive).classList.remove('active');
        }
        this.setState({'NavItemActive':x},()=>{ 
            document.getElementById(this.state.NavItemActive).classList.add('active');
        });
    };

    showResults(e) {
        const val = e.target.value;
        if (val === '') {
            this.setState({
                searching: false
            })
        }
        else {
            this.setState({
                search: val,
                searching: true
            });
            this.props.getSearchResults('name',val);
        }
    }

    render() {
        return (
            <div>
                <nav>
                    <ul>
                        <Navitem item="Home" tolink="/"  activec={this.activeitem}></Navitem>
                        <Navitem item="Register" tolink="/register"  activec={this.activeitem}></Navitem>
                        <Navitem item="Login" tolink="/login"  activec={this.activeitem}></Navitem>
                        <Navitem item="Orders" tolink="/orders"  activec={this.activeitem}></Navitem>
                        <Navitem item="Book Details" tolink="/bookdetails"  activec={this.activeitem}></Navitem>
                        <i class="material-icons md-dark md-24">shopping_cart</i>
                        {/*<Dropdown>
                            <Dropdown.Toggle variant="succes" id="dropdown-basic">
                                Browse
                            </Dropdown.Toggle>
                            <Dropdown.Menu>
                                <Dropdown.Item href="#/fantasy">Fantasy</Dropdown.Item>
                                <Dropdown.Item href="#/mystery">Mystery</Dropdown.Item>
                                <Dropdown.Item href="#/science">Science</Dropdown.Item>
                            </Dropdown.Menu>
                        </Dropdown>*/}
                    </ul>
                </nav>
            </div>
            )
        }
    }

    export default Navbar
