import React, { Component } from 'react';
import book from '../images/book.svg';
import '../style/Gallery.css';

// TODO: Can this component be functional instead?
class Gallery extends Component {

  render() {
    return (
      <div>{
        this.props.items.map( (item, index) => {
          let { title, imageLinks, infoLink, authors, averageRating, dimensions	} = item.volumeInfo;
         let { retailPrice	} = item.saleInfo;
          return (
            <a
              key={index}
              className="book"
              href={infoLink}
              target="_blank"
              rel="noopener"
              >
              <div className="container">
                <img
                  src={undefined !== imageLinks ? imageLinks.thumbnail : {book}}
                  alt={`Pictured: The cover for the book ${title}.`}
                  //className="bookCover"
                />
                <div className="desc">
                  <h2>{title}</h2>
                  <h3>Author: {authors}</h3> 
                  <p>Raiting: {averageRating}</p>    
                  <p>Price: {retailPrice}</p>         
                </div>
              </div>

              {/*<div className="bookTitle">
                <header>{title}</header>
                <h2>{author}</h2>
              </div>*/}
            </a>
          )
        })
      }</div>
    )
  }
}

export default Gallery;
